import React from "react";
import "./scss/App.scss";
import Member from "./components/member";
import allMembers from "./components/allMembers.js";
import {
  Container,
  Row,
  Col,
  Visible,
  Hidden,
  setConfiguration
} from "react-grid-system";

import NominationForm from "./components/nominationForm";
import axios from "axios";
const API_URL = "http://localhost:8000/api/members";

setConfiguration({ gridColumns: 20 });

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numFilters: 0,
      UXDesigner: false,
      VisDesigner: false,
      ProdDesigner: false,
      ContentStrategist: false,
      UXResearcher: false,
      UXEngineers: false,
      Graphic: false,
      allCommunity: allMembers
    };

    this.handleChange = this.handleChange.bind(this);
    this.allMemberMap = this.allMemberMap.bind(this);
    this.resetFilters = this.resetFilters.bind(this);
  }

  handleChange(event) {
    console.log(event.target.name);
    const name = event.target.name;
    let value = !event.target.checked;

    if (value === true) {
      this.state.numFilters++;
    } else {
      this.state.numFilters--;
    }

    this.setState({
      [name]: value
    });

    console.log(this.state);
  }

  resetFilters() {
    console.log("RESET FILTERS");

    this.setState({
      UXDesigner: false,
      VisDesigner: false,
      ProdDesigner: false,
      ContentStrategist: false,
      UXResearcher: false,
      UXEngineers: false,
      Graphic: false,
      numFilters: 0
    });

    console.log(this.state);
  }

  allMemberMap() {
    return this.state.allCommunity.map(member => {
      if (this.state.numFilters < 0) {
        if (member.tags.includes("UXDesigner")) {
          return (
            <Col xs={10} sm={5} lg={4}>
              <Member
                image={member.image}
                name={member.name}
                year={member.year}
                portfolio={member.portfolio}
                linkedin={member.linkedin}
              ></Member>
            </Col>
          );
        }
      } else {
        return (
          <Col xs={10} sm={5} lg={4}>
            <Member
              image={member.image}
              name={member.name}
              year={member.year}
              portfolio={member.portfolio}
              linkedin={member.linkedin}
            ></Member>
          </Col>
        );
      }
    });
  }

  render() {
    return (
      <div className="container">
        {console.log("RENDER AGAIN")}
        <Row>
          <Col xs={20} md={5}>
            <img src={require("./images/designCommunity.svg")}></img>
            <button type="button">NOMINATE</button>

            <div id="filter">
              <h3>Filters</h3>
              <h4 onClick={this.resetFilters}>Clear All</h4>
              <br />
            </div>
            {
              //Need to add form action and all
            }

            <label>
              <input
                type="checkbox"
                name="UXDesigner"
                className="checkbox"
                value="UXDesigner"
                onChange={this.handleChange}
              ></input>
              <p>UX DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="VisDesigner"
                className="checkbox"
                onChange={this.handleChange}
              ></input>
              <p>VISUAL DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="ProdDesigner"
                className="checkbox"
                onChange={this.handleChange}
              ></input>
              <p>PRODUCT DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="ContentStrategist"
                className="checkbox"
                onChange={this.handleChange}
              ></input>
              <p>CONTENT STRATEGIST</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="UXResearcher"
                className="checkbox"
                onChange={this.handleChange}
              ></input>
              <p>UX RESEARCHER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="UXEngineer"
                className="checkbox"
                onChange={this.handleChange}
              ></input>
              <p>UX ENGINEER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="Graphic"
                className="checkbox"
                onChange={this.handleChange}
              ></input>
              <p>GRAPHIC DESIGNER</p>
            </label>
            <Row className="sortBy">
              <Col xs={10}>
                <h3>Sort By</h3>
              </Col>
              <Col xs={10}>
                <label>
                  <p>YEAR</p>
                  <select
                    id="year"
                    onChange={() => {
                      console.log("selected");
                    }}
                  >
                    <option value="ASCENDING">ASCENDING</option>
                    <option value="ASCENDING">DESCENDING</option>
                  </select>
                </label>
              </Col>
            </Row>
          </Col>
          <Col xs={20} md={15}>
            <Row>{this.allMemberMap()}</Row>
          </Col>
        </Row>
      </div>
    );
  }
}

export default App;
