import React from "react";
import "./scss/App.scss";
import Member from "./components/member";
import allMembers from "./components/allMembers.js";
import {
  Container,
  Row,
  Col,
  Visible,
  Hidden,
  setConfiguration
} from "react-grid-system";

import NominationForm from "./components/nominationForm";
import axios from "axios";
const API_URL = "http://localhost:8000/api/members";

setConfiguration({ gridColumns: 20 });

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      UXDesigner: true,
      VisDesigner: true,
      ProdDesigner: true,
      ContentStrategist: true,
      UXResearchers: true,
      UXEngineers: true,
      Graphic: true,
      allCommunity: allMembers
    };

    this.handleChange = this.handleChange.bind(this);
    this.allMemberMap = this.allMemberMap.bind(this);
  }

  handleChange(event) {
    console.log(event.target.name);
    const name = event.target.name;
    let value = !event.target.checked;

    this.setState({
      [name]: value
    });

    console.log(this.state.UXDesigner);
    this.allMemberMap();
  }

  allMemberMap() {
    return this.state.allCommunity.map(member => {
      console.log(member);

      return (
        <Col xs={10} md={4}>
          <Member
            image={member.image}
            name={member.name}
            year={member.year}
            portfolio={member.portfolio}
            linkedin={member.linkedin}
          ></Member>
        </Col>
      );
    });
  }

  render() {
    return (
      <div className="container">
        <Row>
          <Col xs={20} md={5}>
            <img src={require("./images/designCommunity.svg")}></img>
            <button type="button">NOMINATE</button>
            <h3>Filters</h3>

            {
              //Need to add form action and all
            }

            <label>
              <input
                type="checkbox"
                name="UXDesigner"
                className="checkbox"
                value="UXDesigner"
                onChange={this.handleChange}
              ></input>
              <p>UX DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="VisDesigner"
                className="checkbox"
              ></input>
              <p>VISUAL DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="ProdDesigner"
                className="checkbox"
              ></input>
              <p>PRODUCT DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="ContentStrategist"
                className="checkbox"
              ></input>
              <p>CONTENT STRATEGIST</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="UXResearcher"
                className="checkbox"
              ></input>
              <p>UX RESEARCHER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="UXEngineer"
                className="checkbox"
              ></input>
              <p>UX ENGINEER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="Graphic"
                className="checkbox"
              ></input>
              <p>GRAPHIC DESIGNER</p>
            </label>
            <Row className="sortBy">
              <Col xs={10}>
                <h3>Sort By</h3>
              </Col>
              <Col xs={5}>
                <label>
                  <p>YEAR</p>
                  <select id="year">
                    <option value="ASCENDING">ASCENDING</option>
                  </select>
                </label>
              </Col>
            </Row>
          </Col>
          <Col xs={20} md={15}>
            <Row>{this.allMemberMap()}</Row>
          </Col>
        </Row>
      </div>
    );
  }
}

export default App;
