import React from "react";
import "./scss/App.scss";
import Member from "./components/member";
import allMembers from "./components/allMembers.json";
import {
  Container,
  Row,
  Col,
  Visible,
  Hidden,
  setConfiguration
} from "react-grid-system";

import NominationForm from "./components/nominationForm";
import axios from "axios";
const API_URL = "http://localhost:8080/api/members";

setConfiguration({ gridColumns: 20 });

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      UXDesigner: false,
      allCommunity: []
    };
  }

  componentDidMount =()=>{
    console.log("DID MOUNT")
    axios.get(API_URL).then((res)=>{
      for(let i=0; i<res.data.length; i++){
        this.setState({
          allCommunity: [...this.state.allCommunity, res.data[i]]
        });
      }
      console.log(this.state.allCommunity)
    }).catch((err)=>{
      console.log(err);
    })
    console.log("DONE")
  }
  render() {


    return (
      <div className="container">
        <Row>
          <Col xs={20} md={5}>
            <img src={require("./images/designCommunity.svg")}></img>
            <button type="button">NOMINATE</button>
            <h3>Filters</h3>

            {
              //Need to add form action and all
            }

            <label>
              <input
                type="checkbox"
                name="UXDesigner"
                onChange={() => {
                  this.setState({ UXDesigner: !this.state.UXDesigner });
                }}
                className="checkbox"
              ></input>
              <p>UX DESIGNER</p>
            </label>
