                name="UXDesigner"
                onChange={() => {
                  this.setState({ UXDesigner: !this.state.UXDesigner });
                }}
                className="checkbox"
              ></input>
              <p>UX DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="UXDesigner"
                onChange={() => {
                  this.setState({ UXDesigner: !this.state.UXDesigner });
                }}
                className="checkbox"
              ></input>
              <p>UX DESIGNER</p>
            </label>
            <label>
              <input
                type="checkbox"
                name="UXDesigner"
                onChange={() => {
                  this.setState({ UXDesigner: !this.state.UXDesigner });
                }}
                className="checkbox"
              ></input>
              <p>UX DESIGNER</p>
            </label>
            <h3>Sort By</h3>
            <NominationForm />
          </Col>
          <Col xs={20} md={15}>
            <Row>{
              this.state.allCommunity.map(member=>(
                <Col xs={10} md={4}>
                  <Member
                    image={member.image}
                    name={member.name}
                    year={member.year}
                    portfolio={member.portfolio}
                    linkedin={member.linkedin}
                  ></Member>
                </Col>
              ))
            }</Row>
          </Col>
        </Row>
      </div>
    );
  }
}

export default App;
